\begin{aligned}
x_1&amp;amp;= Ax_0+Bu_0\\
x_2&amp;amp;= Ax_1+Bu_1\\
&amp;amp;= A^2x_0+ABu_0+Bu_1\\
x_3&amp;amp;= Ax_2+Bu_2\\
&amp;amp;= A^3x_0+A^2Bu_0+ABu_1+Bu_2\\
&amp;amp;\cdots\\
x_i&amp;amp;= A^ix_0+\sum\limits_{k=0}^{(i-1)}A^{(i-1)-k}Bu_k
\end{aligned}
\right.
\quad
\left|\ 
\begin{aligned}
y_0 &amp;amp;= C x_0 + D u_0\\
y_1 &amp;amp;= Cx_1+Du_1\\
&amp;amp;= C A x_0 + C B u_0 + D u_1\\
y_2 &amp;amp;= Cx_2+Du_2\\
&amp;amp;= C A^2 x_0 + C A B u_0 + C B u_1 + D u_2\\
&amp;amp;\cdots\\
y_{i-1} &amp;amp;= CA^{i-1}x_0+\sum\limits_{k=0}^{(i-2)}CA^{(i-2)-k}Bu_k+Du_{i-1}
\end{aligned}
\right.
